package springproject.restuser.implementations;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.data.domain.Page;import org.springframework.data.domain.Pageable;import org.springframework.stereotype.Service;import org.springframework.transaction.annotation.Transactional;import springproject.restuser.models.entity.User;import springproject.restuser.repositories.IUserRepository;import springproject.restuser.services.IUserService;import java.util.List;@Servicepublic class UserServiceImpl implements IUserService {    @Autowired    private IUserRepository userRepository;    @Override    @Transactional(readOnly = true)    public List<User> select(String status) {        return userRepository.findByStatus(status);    }    @Override    @Transactional(readOnly = true)    public Page<User> select(String status, Pageable pageable) {        return userRepository.findByStatus(status, pageable);    }    @Override    public User get(Long id) {        return userRepository.findById(id).orElse(null);    }    @Override    public User insert(User user) {        user.setStatus("CREATED");        return userRepository.save(user);    }    @Override    public User update(User user, Long id) {        User dbUser = userRepository.findById(id).orElse(null);        dbUser.setUsername(user.getUsername());        dbUser.setPassword(user.getPassword());        dbUser.setName(user.getName());        return userRepository.save(dbUser);    }    @Override    public User delete(Long id) {        User user = userRepository.findById(id).orElse(null);        user.setStatus("DELETED");        return userRepository.save(user);    }}