package springproject.restuser.repositories;import org.springframework.data.domain.Page;import org.springframework.data.domain.Pageable;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.stereotype.Repository;import springproject.restuser.models.entity.User;import java.util.List;@Repositorypublic interface IUserRepository extends JpaRepository<User, Long> {    List<User> findByStatus(String status);    Page<User> findByStatus(String status, Pageable pageable);}